import React, { useState } from 'react';
import  Axios from "axios";
import './index.css';
// import Register from './Register';
import { Link } from 'react-router-dom'



function Login() {

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");


  const [loginStatus, setLoginStatus] = useState("");



  const login = () => {
    Axios.post("http://localhost:5050/login",
    {email: email, password: password}).then((response) => {
      // console.log(response)
      if(response.data.message) {
        // console.log(response);
        setLoginStatus(response.data.message)
      } else {
        setLoginStatus(response.data[0].username)
      }
    });
  };


    return(
        <div className="wrapper">
        <div className="registration_form">
            <div className="title">
                Sign-In
            </div>

            <form>
                <div className="form_wrap">
                    <div className="input_grp">

                    </div>
                    <div className="input_wrap">
                        <label >Email Address</label>
                        <input type="email" placeholder='Email Address...' onChange={(e)=> {setEmail(e.target.value)}}/>
                    </div>

                    <div className="input_wrap">
                        <label >Password</label>
                        <input type="password" placeholder='Password...' onChange={(e)=> {setPassword(e.target.value)}}/>
                    </div>


                    <div className="input_wrap">
                        <button onClick={login} type="button" class="submit_btn">Sign In</button>
                    </div>
                </div>
            </form>
            <div className="signin">Don't have a Roqqu account?
                <Link to="/App">Sign-in</Link>
             <div><p>{loginStatus}</p></div>
            </div>
        </div>
    </div>    
    )
}


export default Login;
