import React, {useState} from "react";
import axios from "axios";


function App() {
    const [data, setData] = useState({});
    const [location, setLocation] = useState("")
    // const [latitude, setLatitude] = useState("");
    // const [longitude, setLongitude] = useState("")

  //  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=65b4507ff178b693eac955a556c53f7e`
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=65b4507ff178b693eac955a556c53f7e`

   const searchLocation = (event) => {
    if(event.key === "Enter") {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data);
      })
      setLocation("");
    }

   }

  return (
    <div className="app">
      <div className="search">
        <input 
        value={location} 
        onChange={event => setLocation(event.target.value)}
        onKeyPress={searchLocation} 
        placeholder="Enter Location"
        type="text"/>
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
          </div>
          <div className="temp">
            {data.main ? <h1>{data.main.temp}°F</h1> : null}          
          </div>
          <div className="descripton">
            {data.weather ? <p>{data.weather[0].main}</p> :null}
          </div>
        </div>
        <div className="bottom">
          <div className="feels">
            <p className="bold">60°F</p>
            <p>Feels Like</p>
          </div>
          <div className="humidity">
            <p className="bold">40%</p>
            <p>Humidity</p>
          </div>
          <div className="wind">
            <p className="bold">12MPH</p>
            <p>Wind Speed</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
