import React, { useState } from "react";
import  Axios from "axios";
import './Form.css';
import "./index.css";
import { GrMenu }from "react-icons/gr"
import { FaTimes } from "react-icons/fa";
import { Link } from "react-router-dom";


function Profile() {

    const [firstnameUpdate, setFirstname] = useState("");
    const [lastnameUpdate, setLastname] = useState("");
    const [emailUpdate, setEmail] = useState("");
    const [passwordUpdate, setPassword] = useState("");
    const [open, setOpen] = useState(false);



    const change = () => {
        // if(!firstnameReg || lastnameReg || emailReg || passwordReg) {
        //    console.log({Error: Error})
        // } else {
            console.log("working")
      Axios.post("http://localhost:5050/register",

      {firstname: firstnameUpdate, lastname: lastnameUpdate, email: emailUpdate, password: passwordUpdate}).then((response) => {    
        if(response.data.status === "success") {
            alert("Changes saved")
            // window.location.href = "/weather"
        }
        console.log("Response is not successful")
        // console.log(response)
        // console.log(response.data);
        // console.log(response.data.status)
      })
    //   register("");
    //   }
     };

    return(
        <div>
          <div className="menu-bar" onClick={() => setOpen(true)}>
            <GrMenu width={40} height={40} className="bar"/>
          {/* <Image src={menu} alt="" width={40} height={40} /> */}
        </div>
        <div className={`sidenav ${open && "increaseWidth"}`}>
          <p className="close" onClick={() => setOpen(false)}>
            <FaTimes />
          </p>
          <ul id="navbar">
                <li><Link class="active" to="/weather">Home</Link></li>
                <li><Link class="active" to="/profile">Profile</Link></li>
                <li><Link class="active" to="index.html">History</Link></li>
          </ul>     
           {/* <button type="" className="my_btn" onClick={click}>Profile</button> */}
        </div>
        {open && <div className="opaque" onClick={() => setOpen(false)}></div>}

        <div className="wrapper">
        <div className="registration_form">

        <div className="title">
            profile

        </div>

        <form>
            <div className="form_wrap">
                <div className="input_grp">
                    <div className="input_wrap">
                        <label>First Name </label>
                        <input type="text"  placeholder="First Name . . ." />
                    </div>
                    <div className="input_wrap">
                        <label >Last Name  </label>
                        <input type="text"  placeholder="Last Name . . ." />
                    </div>
                </div>
                <div className="input_wrap">
                    <label >Email Address </label>
                    <input type="email"  placeholder="Email Address . . ." />
                </div>

                {/* <div className="input_wrap">
                    <label >Password</label>
                    <input type="password"  placeholder="password . . ." />
                </div> */}


                <div className="input_wrap">
                    <button onClick={change} type="button"  className="submit_btn" >Save Changes</button>
                </div>
            </div>
        </form>                        
        </div>       
      </div>
      </div>   

    )
}

export default Profile
