import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css';
import { GrMenu } from 'react-icons/gr';
import { FaTimes } from 'react-icons/fa';
import { Link } from 'react-router-dom';
// import 'antd/dist/antd.css';
// import { Button } from 'antd';
// import { FaHistory } from "react-icons/fa";
// import { FaSearchLocation } from "react-icons/fa"
// import { AiFillHome } from "react-icons/ai"
// import { ImUser } from "react-icons/im"

function Weather() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');
  const [open, setOpen] = useState(false);



 var firstnameUpdate = localStorage.firstname
 var lastnameUpdate = localStorage.lastname
 var passwordUpdate = localStorage.password
 var userEmail = localStorage.email
//  var userPassword = localStorage.password 

  // const [city, setCity] = useState ('');
  // const [weather, setWeather] = useState (''); //
  // const [feels_like, setFeels] = useState ('');
  // const [temperature, setTemperature] = useState ('');
  // const [humidity, setHumidity] = useState ('');
  // const [wind_speed, setWindSpeed] = useState ('');

  //  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=65b4507ff178b693eac955a556c53f7e`
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=65b4507ff178b693eac955a556c53f7e`;
  useEffect(() => {
    axios.get("http://localhost:5050/getUser")
    .then(res => console.log(res))
    .catch(err => console.log(err))
}, []);

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then(response => {
        const data = (response.data)
        setData(data);

        const save = (payload) => {
          axios.post('http://localhost:5050/weather', payload).then(response => {
              if (response.data.status === 'success') {
                alert('success');                
                console.log(userEmail)
                localStorage.setItem("userData", data)
                localStorage.setItem("city", data.name)
                localStorage.setItem("temperature", data.main.temp.toFixed())
                localStorage.setItem("wind_speed", data.wind.speed.toFixed())
                localStorage.setItem("weather", data.weather[0].main)                
              }
              console.log('Response is not successful');
              console.log(userEmail)
              // console.log(userPassword)
              console.log(firstnameUpdate);
              console.log(lastnameUpdate);
              console.log(passwordUpdate)    
            });
        };
        save({ city: data.name, temperature: data.main.temp.toFixed(), humidity: data.main.humidity, feels_like: data.main.feels_like.toFixed(), wind_speed: data.wind.speed.toFixed(), weather: data.weather[0].main, email:  userEmail})
        console.log(response.data);
      });
      setLocation('');
    }
  };

  //  const getHistory = () => {
  //   componentDidMount(){
  //     axios.get("http://localhost:5050/weather", 
  //     {city: data.name, temp: data.main.temp.toFixed(), weather: data.weather[0].main, wind_speed: data.wind.speed.toFixed()})
  //    };
  //  }

  // const click = () => {
  //   window.location.href = "/profile"
  // }

  return (
    <div className="app">
      <div className="search">
        <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder="Enter Location"
          type="text"
        />        
      </div>
      <div className='link'>
      {/* <Link to="/profile">{userFirstName}  {userLastName}</Link> */}
      </div>

      <input type="" name="" value= {firstnameUpdate}/>
        <input type="" name="" value={lastnameUpdate} />
          <div className="menu-bar" onClick={() => setOpen(true)}>
            <GrMenu width={40} height={40} className="bar" />  
          </div>


      <div className={`sidenav ${open && 'increaseWidth'}`}>
        <p className="close" onClick={() => setOpen(false)}>
          <FaTimes />
        </p>
        <ul id="navbar">
          <li><Link className="active" to="/weather">Home</Link></li>
          <li><Link className="active" to="/profile">Profile</Link></li>
          <li><Link className="active" to="/history">History</Link></li>
          <div className='button'>
            <button type="" className='logout-btn'>Log Out</button>
          </div>
        </ul>
        {/* <button type="" className="my_btn" onClick={click}>Profile</button> */}
      </div>
      {open && <div className="opaque" onClick={() => setOpen(false)} />}

      <div className="container">
        {data.name !== undefined &&
          <div className="top">
            <div className="top-style">
              <div className="location">
                <p className="bold">{data.name} </p>
                {/* <p>{data.sys.country}</p> */}
              </div>
              <div className="temp">
                {/* <p className="name">Currently</p> */}
                {data.main ? <h1>{data.main.temp.toFixed()}°C</h1> : null}
              </div>
              <div className="descripton">
                {data.weather
                  ? <p className="bold">{data.weather[0].main} </p>
                  : null}
                {/* {data.weather ? <p>{data.weather.icon}</p> : null}             */}
              </div>
              <div className="few-Description">
                {data.weather
                  ? <p className="bold">{data.weather[0].description}</p>
                  : null}
              </div>
            </div>
          </div>}

        {data.name !== undefined &&
          <div className="bottom">
            <div className="feels">
              {data.main
                ? <p className="bold">{data.main.feels_like.toFixed()}°C</p>
                : null}
              <p>Feels Like</p>
            </div>
            <div className="humidity">
              {data.main
                ? <p className="bold">{data.main.humidity}% </p>
                : null}
              <p>Humidity</p>
            </div>
            <div className="wind">
              {data.wind
                ? <p className="bold">{data.wind.speed.toFixed()}MPH</p>
                : null}
              <p>Wind Speed</p>
            </div>
          </div>}

        {/* {data.name !== undefined && 
       <div className="second-bottom">
       <div className="feels">
         {data.main ? <p className="bold">{data.main.pressure}</p> : null}
         <p></p>
       </div>
       <div className="humidity">           
         {data.main ? <p className="bold">{data.main.temp_min}°C</p> : null}
         <p>Temp-Min</p>
       </div>
       <div className="wind">
       {data.wind ? <p className="bold">{data.main.temp_max}°C</p> : null}
         <p>Temp-Max</p>
       </div>
     </div> 
}       */}

      </div>
    </div>
  );
}

export default Weather;
