import axios from "axios";
import React, {useState} from "react";
import { FaLock } from "react-icons/fa";

function SavePassword() {

     const [password, setPassword] = useState("")
     const [secondPassword, setSecondPassword] = useState("")

    axios.post("http://localhost:5050/password-update", 
    {password: password})

    const reset = () => {
      if(secondPassword === password) {
          alert("passowrd has been changed successfuly")
          window.location.href = "/login"
      } else{
        alert("Password does not match, try again")
      }
    }
    // .then((response) => {
    //   console.log(response)
    //   window.location.href = "/login"
    // })


  return (
    <div className="input">
      <div className="wrapper">
        <div className="registration_form">
          <div className="title">Password Resest</div>

          <form>
            <div className="form_wrap">
              <div className="input_grp" />
              <div className="input_wrap">
                <label>
                  Password <FaLock />
                </label>
                <input type="password" placeholder="Email Address..."  onChange={(e) => setPassword(e.target.value)}/>
              </div>
              <div className="input_wrap">
                <label>
                  Confirm Password <FaLock />
                </label>
                <input type="password" placeholder="Email Address..." onChange={(e) => setSecondPassword(e.target.value)}/>
              </div>
              <div className="input_wrap">
                <button onClick={reset} type="button" className="submit_btn">
                  Save
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SavePassword;
