import React, {useState} from "react";
import axios from "axios";


function App() {
    const [data, setData] = useState({});
    const [location, setLocation] = useState("")
    // const [weatherIcon, setWeatherIcon] = useState(`${process.env.REACT_APP_ICON_URL}10n@2x.png`)
    // const [latitude, setLatitude] = useState("");
    // const [longitude, setLongitude] = useState("")

  //  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=65b4507ff178b693eac955a556c53f7e`
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=65b4507ff178b693eac955a556c53f7e`

   const searchLocation = (event) => {
    if(event.key === "Enter") {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data);
      })
      setLocation("");
    }

   }

  return (
    <div className="app">
      <div className="search">
        <input 
        value={location} 
        onChange={event => setLocation(event.target.value)}
        onKeyPress={searchLocation} 
        placeholder="Enter Location"
        type="text"/>
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
            {/* <p>{data.sys.country}</p> */}
          </div>
          <div className="temp">
            {/* <p className="name">Currently</p> */}
            {data.main ? <h1>{data.main.temp.toFixed()}°C</h1> : null}          
          </div>
          <div className="descripton">
            {data.weather ? <p>{data.weather[0].main}</p> :null}
            {/* {data.main ? <p>{data.weather[0].icon}</p> : null} */}

          </div>
        </div>

        {data.name !== undefined && 
       <div className="bottom">
       <div className="feels">
         {data.main ? <p className="bold">{data.main.feels_like.toFixed()}°C</p> : null}
         <p>Feels Like</p>
       </div>
       <div className="humidity">           
         {data.main ? <p className="bold">{data.main.humidity}%</p> : null}
         <p>Humidity</p>
       </div>
       <div className="wind">
       {data.wind ? <p className="bold">{data.wind.speed.toFixed()}MPH</p> : null}
         <p>Wind Speed</p>
       </div>
     </div> 
}


      </div>
    </div>
  );
}

export default App;
