import React, {useState} from "react";
import axios from "axios";
import "./index.css";
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
// import {regular} from '@fortawesome/fontawesome-svg-core/import.macro' // <-- import styles to be used
import { FaHistory } from "react-icons/fa";
// import { FaSearchLocation } from "react-icons/fa"

function Weather() {
    const [data, setData] = useState({});
    const [location, setLocation] = useState("")
    // const [weatherIcon, setWeatherIcon] = useState(`${process.env.REACT_APP_ICON_URL}10n@2x.png`)
    // const [latitude, setLatitude] = useState("");
    // const [longitude, setLongitude] = useState("")

  //  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=65b4507ff178b693eac955a556c53f7e`
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=65b4507ff178b693eac955a556c53f7e`

   const searchLocation = (event) => {
    if(event.key === "Enter") {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data);
      })
      setLocation("");
    }

   }

  return (
    <div className="app">
      <div className="search">
        <input 
        value={location} 
        onChange={event => setLocation(event.target.value)}
        onKeyPress={searchLocation} 
        placeholder="Enter Location"        
        type="text"/>
        {/* <FaSearchLocation /> */}
        {/* <FaHistory /> */}
        <button class="btn btn-primary" type="button" data-bs-toggle="offcanvas" data-bs-target="#offcanvasRight" aria-controls="offcanvasRight"> <FaHistory /></button>
     <div class="offcanvas offcanvas-end" tabindex="-1" id="offcanvasRight" aria-labelledby="offcanvasRightLabel">
     <div class="offcanvas-header">
     <h5 class="offcanvas-title" id="offcanvasRightLabel">Offcanvas right</h5>
       <button type="button" class="btn-close" data-bs-dismiss="offcanvas" aria-label="Close"></button>
      </div>
      <div class="offcanvas-body">
       working
     </div>
      </div>
      </div>


      <div className="container">
      {data.name !== undefined && 
        <div className="top">
          <div className="top-style">                      
          <div className="location">
            <p className="bold">{data.name}</p>
            {/* <p>{data.sys.country}</p> */}
          </div>
          <div className="temp">
            {/* <p className="name">Currently</p> */}
            {data.main ? <h1>{data.main.temp.toFixed()}°C</h1> : null}          
          </div>
          <div className="descripton">
            {data.weather ? <p className="bold">{data.weather[0].main}</p> :null}
            {data.weather ? <p>{data.weather[0].icon}</p> : null}            
          </div>
          <div className="few-Description">
             {data.weather ? <p className="bold">{data.weather[0].description}</p> : null}
          </div>
        </div>
        </div> 
      }


        {data.name !== undefined && 
       <div className="bottom">
       <div className="feels">
         {data.main ? <p className="bold">{data.main.feels_like.toFixed()}°C</p> : null}
         <p>Feels Like</p>
       </div>
       <div className="humidity">           
         {data.main ? <p className="bold">{data.main.humidity}%</p> : null}
         <p>Humidity</p>
       </div>
       <div className="wind">
       {data.wind ? <p className="bold">{data.wind.speed.toFixed()}MPH</p> : null}
         <p>Wind Speed</p>
       </div>
     </div> 
}

     {/* {data.name !== undefined && 
       <div className="second-bottom">
       <div className="feels">
         {data.main ? <p className="bold">{data.main.pressure}</p> : null}
         <p></p>
       </div>
       <div className="humidity">           
         {data.main ? <p className="bold">{data.main.temp_min}°C</p> : null}
         <p>Temp-Min</p>
       </div>
       <div className="wind">
       {data.wind ? <p className="bold">{data.main.temp_max}°C</p> : null}
         <p>Temp-Max</p>
       </div>
     </div> 
}       */}




      </div>
    </div>
  );
}

export default Weather;
