import React, {useState} from "react";
import axios from "axios";
import "./index.css";
import { GrMenu }from "react-icons/gr"
import { FaTimes } from "react-icons/fa"
// import { FaHistory } from "react-icons/fa";
// import { FaSearchLocation } from "react-icons/fa"

function Weather() {
    const [data, setData] = useState({});
    const [location, setLocation] = useState("")    
    const [open, setOpen] = useState(false);
    // const [city, setCity] = useState("");
    // const [weather, setWeather] =  useState("");          // 
    // const [feels_like, setFeels] = useState("");
    // const [temperature, setTemperature] = useState("");
    // const [humidity, setHumidity] = useState("");
    // const [wind_speed,setWindSpeed] = useState("");


    // const find = () => {
    //   axios.post("http://localhost:5050/weather",
    //   {city :city, weather: weather, temperature: temperature, humidity: humidity, wind_speed: wind_speed, feels_like: feels_like}).then((response) => {
    //     if(response.data.status === "success") {
    //       alert("success")
    //       // window.location.href = "/weather"
    //   }
    //   console.log("Response is not successful")
    //   })
    // }

  //  const url = `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=65b4507ff178b693eac955a556c53f7e`
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=65b4507ff178b693eac955a556c53f7e`

   const searchLocation = (event) => {
    if(event.key === "Enter") {
      axios.get(url).then((response) => {
        setData(response.data)
        // setData(find)
        console.log(response.data);
      })
      setLocation("");
    }

   }

  return (
    <div className="app">
      <div className="search">
        <input 
        value={location} 
        onChange={event => setLocation(event.target.value)}
        onKeyPress={searchLocation} 
        placeholder="Enter Location"        
        type="text"/>
        {/* <FaHistory /> */}
      </div>
      {/* <div className="icon">
      <FaSearchLocation className="search" onClick={searchLocation}/>
      </div> */}
      <div className="menu-bar" onClick={() => setOpen(true)}>
            <GrMenu width={40} height={40} className="bar"/>
          {/* <Image src={menu} alt="" width={40} height={40} /> */}
        </div>
        <div className={`sidenav ${open && "increaseWidth"}`}>
          <p className="close" onClick={() => setOpen(false)}>
            <FaTimes />
          </p>


        </div>
        {open && <div className="opaque" onClick={() => setOpen(false)}></div>}


      <div className="container">
      {data.name !== undefined && 
        <div className="top">
          <div className="top-style">                      
          <div className="location">
            <p className="bold">{data.name}</p>
            {/* <p>{data.sys.country}</p> */}
          </div>
          <div className="temp">
            {/* <p className="name">Currently</p> */}
            {data.main ? <h1>{data.main.temp.toFixed()}째C</h1> : null}          
          </div>
          <div className="descripton">
            {data.weather ? <p className="bold">{data.weather[0].main}</p> :null}
            {data.weather ? <p>{data.weather.icon}</p> : null}            
          </div>
          <div className="few-Description">
             {data.weather ? <p className="bold">{data.weather[0].description}</p> : null}
          </div>
        </div>
        </div> 
      }


        {data.name !== undefined && 
       <div className="bottom">
       <div className="feels">
         {data.main ? <p className="bold">{data.main.feels_like.toFixed()}째C</p> : null}
         <p>Feels Like</p>
       </div>
       <div className="humidity">           
         {data.main ? <p className="bold">{data.main.humidity}%</p> : null}
         <p>Humidity</p>
       </div>
       <div className="wind">
       {data.wind ? <p className="bold">{data.wind.speed.toFixed()}MPH</p> : null}
         <p>Wind Speed</p>
       </div>
     </div> 
}

     {/* {data.name !== undefined && 
       <div className="second-bottom">
       <div className="feels">
         {data.main ? <p className="bold">{data.main.pressure}</p> : null}
         <p></p>
       </div>
       <div className="humidity">           
         {data.main ? <p className="bold">{data.main.temp_min}째C</p> : null}
         <p>Temp-Min</p>
       </div>
       <div className="wind">
       {data.wind ? <p className="bold">{data.main.temp_max}째C</p> : null}
         <p>Temp-Max</p>
       </div>
     </div> 
}       */}




      </div>
    </div>
  );
}

export default Weather;
