import React, {useState, useEffect} from 'react';
import Axios from 'axios';
import './index.css';
import {Link} from 'react-router-dom';
import {FaLock} from 'react-icons/fa';
import {GrMail} from 'react-icons/gr';
// import axios from 'axios';
// FaLock GrMail

function Login () {
  const [email, setEmail] = useState ('');
  const [password, setPassword] = useState ('');

  // email = localStorage.getItem(email)
  // const [isPending, setIsPending] = useState(true);
  //const [loginStatus, setLoginStatus] = useState("");
  // const [data, setData] = useState({})

  const login = () => {
    console.log ('login');
    Axios.post ('http://localhost:5050/login', {
      email: email,
      password: password,
    })
      .then (response => {
        console.log ({response});

        // axios.get("http://localhost:5050/login")
        localStorage.setItem ('email', email);
        localStorage.setItem ('password', password);
        console.log (localStorage.password);
        console.log (localStorage.email);
        // window.location.href = "/weather"
      })
      .catch (err => console.log (err));
  };
  const url = 'http://localhost:5050/getUser';
  useEffect (() => {
    Axios.post (url)
      .then (response => {
        console.log (response);
        // const data = (res.data)
        // setData(data)
        const {firstname, lastname} = response;
        console.log (firstname);
        console.log (lastname);
        // const firstname = (data.firstname)
        // const lastname = (data.lastname)
        localStorage.seItem ('fname', firstname);
        localStorage.setItem ('lname', lastname);
        console.log (response);
        response ();
      })
      .catch (err => console.log (err));
  }, []);

  //   const firstname = (data.firstname)
  //     const lastname = (data.lastname)
  //     localStorage.setItem("fname", firstname)
  //     localStorage.setItem("lname", lastname)

  return (
    <div className="input">
      <div className="wrapper">
        <div className="registration_form">
          <div className="title">
            Sign-In
          </div>

          <form>
            <div className="form_wrap">
              <div className="input_grp" />
              <div className="input_wrap">
                <label>Email Address <GrMail /></label>
                <input
                  type="email"
                  placeholder="Email Address..."
                  onChange={e => {
                    setEmail (e.target.value);
                  }}
                />
              </div>

              <div className="input_wrap">
                <label>Password <FaLock /></label>

                <input
                  type="password"
                  placeholder="Password..."
                  onChange={e => {
                    setPassword (e.target.value);
                  }}
                />

              </div>

              <div className="input_wrap">
                <button onClick={login} type="button" className="submit_btn">
                  Sign In
                </button>
              </div>
            </div>
          </form>
          <div className="signin">
            Don't have a Roqqu account?
            <Link to="/">Sign-up</Link>
            {/* <div><p>{loginStatus}</p></div> */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
